import React from 'react';
import { useState } from 'react';


const EmployeesList= () =>{

    const [fields,setfields]=useState( {
        Code:'',
        Name:'',
        Designation:'',
    });

    const [fieldsArr,setfieldsArr]=useState( [{
        Code:'',
        Name:'',
        Designation:'',
    }]);

const SetValueInHeading= (event) => {

    const value=event.target.value;
    const name=event.target.name;
    
    setfields ((preValue) =>{
        return{
            ...preValue,
            [name]:value,
        
        }
    })
    }



const SetValueInHeadingAfterSubmit= 
(eventSetValueInHeadingAfterSubmit) => {

eventSetValueInHeadingAfterSubmit.preventDefault();


setfieldsArr([...fieldsArr, fields]);
}


return(
<>

<table className='styled-table'>
    <thead>
        <tr>
            <th>Code</th>
            <th>Name</th>
            <th>Designation</th>
            <th>Action</th>
        </tr>
    </thead>
    <tbody>
        {
            fieldsArr.map((employee,index)=>{
                if(employee.Code != null && employee.Code != ''){
                return(
                <>
               <tr key={employee.index}>
                <td>{employee.Code}</td>
                <td>{employee.Name}</td>
                <td>{employee.Designation}</td>
                <td><a href='#' data-attr={employee.index}>Update</a> <a href='#'>delete</a></td>
                </tr>
                </>
                )
            }
            })
        }
       

    </tbody>
</table>

<hr className="new1" style={{borderTop:'1px solid red'}}/>
<hr className="new1" style={{borderTop:'1px solid red'}}/>


<div className='Red-background'>
<form onSubmit={SetValueInHeadingAfterSubmit}> 
  <h2>Create Employees</h2>
  <input type="text" name='Code' onChange={SetValueInHeading} placeholder="Enter employee code" required/>  <br/> <br/>
  <input type="text" name='Name' onChange={SetValueInHeading} placeholder="Enter employee name" required/>  <br/> <br/>
  <input type="text" name='Designation' onChange={SetValueInHeading} placeholder="Enter employee designation" required/>  <br/> <br/>
  <br/> <br/>
  <button type='submit' className='button-1' role="button">Submit</button>
</form>
<br/> <br/>
</div>
        </>
    )
}


export default EmployeesList;
