import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from 'react-router-dom';
  // import Home component
import CovidTracker from './CovidTracker';
import { Link } from 'react-router-dom';
import ManageEmployeeForm from './ManageEmployeeForm';


function App(){
  return(
  <>
   {/* This is the alias of BrowserRouter i.e. Router */}
   <Router>
        <Routes>
          {/* This route is for home component 
          with exact path "/", in component props 
          we passes the imported component*/}
          {/* <Route exact path='/' component={CovidTracker} /> */}




          {/* If any route mismatches the upper 
          route endpoints then, redirect triggers 
          and redirects app to home component with to="/" */}
          {/* <Navigate to="/" /> */}



        <Route path="/CovidTracker" element={<CovidTracker />} />
          {/* Endpoint to route to About component */}
          <a href='/CovidTracker'>Covid Tracker</a>

        {/* <Route path="/" element={<Navigate replace to="/home" />} /> */}
        </Routes>
      </Router>


  <div>
     <h1 style={{margin:'15px'}}>Manage Employees</h1>
     <ManageEmployeeForm/>
     {/* <ManageEmployeeForm/> */}
   </div>
  </>
  )
}


export default App;
