         eventSetValueInHeadingAfterSubmit.target.getAttribute('data-Code'));

       
         const removed = fieldsArr.filter(item => item.Code !== eventSetValueInHeadingAfterSubmit.target.getAttribute('data-Code'));
         setfieldsArr([removed])


         fieldsArr_new[0].Code= fields.Code;
         fieldsArr_new[0].Name= fields.Name;
         fieldsArr_new[0].Designation= fields.Designation;
         setfieldsArr([...fieldsArr, fieldsArr_new[0]]);

}



document.getElementsByClassName("code")[0].value = '';
document.getElementsByClassName("name")[0].value = '';
document.getElementsByClassName("designation")[0].value = '';


}


function DeleteRecord(event){
